import React, { useState } from 'react';

function FolderSidebar({ folders, selectedFolder, onSelectFolder, onAddFolder, onDeleteFolder, todos, isOpen, onClose }) {
  const [isAdding, setIsAdding] = useState(false);
  const [newFolderName, setNewFolderName] = useState('');

  // Ìè¥ÎçîÎ≥Ñ Ìï†Ïùº Í∞úÏàò Í≥ÑÏÇ∞
  const getTodoCountByFolder = (folderId) => {
    return todos.filter(todo => todo.folderId === folderId && !todo.completed).length;
  };

  // Ìè¥Îçî Ï∂îÍ∞Ä
  const handleAddFolder = () => {
    if (newFolderName.trim()) {
      onAddFolder(newFolderName.trim());
      setNewFolderName('');
      setIsAdding(false);
    }
  };

  return (
    <>
      {/* Î™®Î∞îÏùº Ïò§Î≤ÑÎ†àÏù¥ */}
      {isOpen && (
        <div className="sidebar-overlay" onClick={onClose}></div>
      )}
      
      <div className={`folder-sidebar ${isOpen ? 'open' : ''}`}>
        <div className="sidebar-header">
          <h1>Ìï†Ïùº Í¥ÄÎ¶¨</h1>
          <button className="close-sidebar-btn" onClick={onClose}>√ó</button>
        </div>

        <div className="folder-list">
          {/* Ï†ÑÏ≤¥ Î≥¥Í∏∞ */}
          <button
            className={`folder-item ${selectedFolder === null ? 'active' : ''}`}
            onClick={() => {
              onSelectFolder(null);
              onClose();
            }}
          >
            <span className="folder-icon" style={{ backgroundColor: '#E0E0E0' }}>üìã</span>
            <span className="folder-name">Ï†ÑÏ≤¥ Î≥¥Í∏∞</span>
            <span className="folder-count">{todos.filter(t => !t.completed).length}</span>
          </button>

          {/* Ìè¥Îçî Î™©Î°ù */}
          {folders.map(folder => (
            <div key={folder.id} className="folder-item-wrapper">
              <button
                className={`folder-item ${selectedFolder === folder.id ? 'active' : ''}`}
                onClick={() => {
                  onSelectFolder(folder.id);
                  onClose();
                }}
              >
                <span 
                  className="folder-icon" 
                  style={{ backgroundColor: folder.color }}
                >
                  üìÅ
                </span>
                <span className="folder-name">{folder.name}</span>
                <span className="folder-count">{getTodoCountByFolder(folder.id)}</span>
              </button>
              <button
                className="delete-folder-btn"
                onClick={(e) => {
                  e.stopPropagation();
                  onDeleteFolder(folder.id);
                }}
                title="Ìè¥Îçî ÏÇ≠Ï†ú"
              >
                √ó
              </button>
            </div>
          ))}

          {/* Ìè¥Îçî Ï∂îÍ∞Ä */}
          {isAdding ? (
            <div className="add-folder-form">
              <input
                type="text"
                value={newFolderName}
                onChange={(e) => setNewFolderName(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleAddFolder()}
                placeholder="Ìè¥Îçî Ïù¥Î¶Ñ"
                autoFocus
              />
              <button onClick={handleAddFolder}>Ï∂îÍ∞Ä</button>
              <button onClick={() => {
                setIsAdding(false);
                setNewFolderName('');
              }}>Ï∑®ÏÜå</button>
            </div>
          ) : (
            <button className="add-folder-btn" onClick={() => setIsAdding(true)}>
              + ÏÉà Ìè¥Îçî
            </button>
          )}
        </div>
      </div>
    </>
  );
}

export default FolderSidebar;
